$command="C:\Program Files (x86)\FAHClient\FAHClient.exe" ; $x="zoom" |ForEach-Object{Get-Process -name $_ | Measure-Object PrivateMemorySize -sum | Select-Object @{Name="Name";Expression = {$pv.name}}, Sum}; Write-Output $x.sum;if ($x.sum -gt 230000000){$arguments="--send-pause";Write-Output "zoom running, pausing FAH";Start-Process $command $arguments} else {$arguments="--send-unpause";Write-Output "zoom not running, time to fold";;Start-Process $command $arguments}